import { Context } from "components/context";
import { useContext } from "react";
import { useState } from "react";
import React, { useRef } from 'react';
import emailjs from '@emailjs/browser';

import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';

import './basket.css'

const Basket = () => {
    let name = localStorage.getItem('name');
    let sum = localStorage.getItem('sum');
    let link = localStorage.getItem('link');
    const {basket} = useContext(Context);

    const [window, setWindow] = useState(true);

    const form = useRef();

    const sendEmail = (e) => {
        e.preventDefault();

        emailjs.sendForm('service_oijkqup', 'template_37g7m4d', form.current, '7Rkc6DbN_xYYcgT5v')
            .then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });
    }

    const cleanerBin = () => {
        localStorage.removeItem('name');
        localStorage.removeItem('sum');
        localStorage.removeItem('link');
        alert('Ваш заказ успешно оформлен')
    }
        const aee = window? <WindowBin/>: false;

    return(
        <>
            <div className="title p-3" style={{"margin-top": "20px"}}>
            <p className="title_caption" style={{"font-size": "40px"}}>Оформление заказа</p>

        <div className="line_title">
            <div className="sqr line_title_first"></div>
        <div className="sqr line_title_second"></div>
        </div>
            </div>

        </>

    )

    const WindowBin = () => {

        return(
            <>
            <Form  style = {{'box-shadow': '0 4px 4px 4px black'}} ref={form} onSubmit={sendEmail}>
                <Row className="row__top p-3">
                <Col>
                    <Form.Label>Послуга</Form.Label>
                    <Form.Control type="text" placeholder={name} value = {name} name="name_cart" readOnly />
                    </Col>
                    <Col>
                    <Form.Label>Ссылка</Form.Label>
                    <Form.Control type="text" placeholder={link} value = {link} name="link" readOnly />
                    </Col>
                    <Col>
                    <Form.Label>Сума $</Form.Label>
                    <Form.Control type="text" placeholder={sum} value = {sum}  name="sum"  readOnly />
                    </Col>
                </Row>
                <Row style = {{'margin-top': '15px'}}>
                    <Col>
                    <Form.Control type = "text" placeholder="Імя"  name="name"/>
                    </Col>
                    <Col>
                    <Form.Control  type = "text" placeholder="Номер телефона"  name="number"/>
                    </Col>
                    <Col>
                    <Form.Control type="email" name="email"  placeholder="name@example.com" />
                    </Col>
                </Row>
                <Col  style = {{'margin-top': '15px', 'color': '#1da1f3'}} xs="auto">
                    <Button  onClick={()=>cleanerBin()} type="submit" className="mb-2 p-1 m-3" >
                         Оформление 
                    </Button>
                    </Col>
          </Form>
        </>
        )
    }
}


export default Basket;
