import { Context } from "components/context";
import { useContext } from "react";
import React, { useRef } from 'react';
import emailjs from '@emailjs/browser';

import './basket.css'

const Basket = () => {
    let name = localStorage.getItem('name');
    let img = localStorage.getItem('img');
    let sum = localStorage.getItem('sum');
    const {basket} = useContext(Context)

    const form = useRef();

    const sendEmail = (e) => {
      e.preventDefault();

      emailjs.sendForm('service_oijkqup', 'template_37g7m4d', form.current, '7Rkc6DbN_xYYcgT5v')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });

    }


    return(
        <>
            <div className="title" style={{"margin-top": "20px"}}>
            <p className="title_caption" style={{"font-size": "40px"}}>Оформление заказа</p>

        <div className="line_title">
            <div className="sqr line_title_first"></div>
        <div className="sqr line_title_second"></div>
        </div>
  </div>
            <form ref={form} onSubmit={sendEmail}>
                <div classNameName="simplecheckout-button-block buttons">
                {name} {img} {sum}
                </div>
                <label>Name</label>
                <input type="text" name="name" />
                <label>Номер телефона</label>
                <input type="text" name="number" />
                <label>Email</label>
                <input type="email" name="email" />
                <input type="submit" value="Send" />
            </form>  
            <a href="#" className="button" style={{"position": "unset", "float": "right"}}><span>Продолжить</span></a> 
        </>
    )
}

export default Basket;
