import { Context } from "components/context";
import { useContext } from "react";

import './basket.css'

const Basket = () => {
    let name = localStorage.getItem('name');
    let img = localStorage.getItem('img');
    let sum = localStorage.getItem('sum');
    const {basket} = useContext(Context)
    return(
        <>
            <div class="title" style={{"margin-top": "-20px"}}>
                    <p class="title_caption" style={{"font-size": "40px"}}>Оформление заказа</p>

                <div class="line_title">
                    <div class="sqr line_title_first"></div>
                <div class="sqr line_title_second"></div>
                </div>
            </div>
            <div className="simplecheckout-button-block buttons">
            {name} {img} {sum}
            </div>  
            <a href="#" class="button" style={{"position": "unset", "float": "right"}}><span>Продолжить</span></a> 
        </>
    )
}

export default Basket;
